/**
	@brief		Automatically refresh the activities table.
	@since		2015-07-22 21:32:29
**/
;(function( $ )
{
    $.fn.extend(
    {
        pvam_autorefresh_activities_table: function()
        {
            return this.each( function()
            {
                var $this = $( this );

                // Only refresh if the table has a timeout.
                var refresh_timeout = $( 'table', $this ).data( 'refresh_timeout' );

                if ( refresh_timeout < 1 )
                	return;

                /**
                	@brief		Contains the timeout.
                	@since		2015-07-22 21:35:58
                **/
                var timeout;

                /**
                	@brief		Refresh the table.
                	@since		2015-07-22 21:34:51
                **/
                $this.refresh = function()
                {
                	pvam_refresh_activities_table( function()
                	{
                		// Reapply the sort.
                		sorttable.makeSortable( $( 'table', $this ).get( 0 ) );
                		$this.set_timeout();
                	} );
                }

                /**
                	@brief		Set the timeout.
                	@since		2015-07-22 21:36:27
                **/
                $this.set_timeout = function()
                {
                	clearTimeout( timeout );
					timeout = setTimeout( function()
					{
						$this.refresh();
					}, refresh_timeout * 60 * 1000 );
                }

                $this.set_timeout();

            }); // return this.each( function()
        } // plugin: function()
    }); // $.fn.extend({
} )( jQuery );
;
/**
	@brief		Ajaxify the selection of filters.
	@since		2015-07-07 16:11:48
**/
;(function( $ )
{
    $.fn.extend(
    {
        pvam_ajax_filters: function()
        {
            return this.each( function()
            {
                var $this = $( this );

				/**
					@brief		Handle the fetching and display of the ajaxed filter settings.
					@since		2015-07-07 20:43:51
				**/
				$this.handle_settings = function( $button )
				{
					var $popup = pvam_popup()
						.set_title( pvam_ajax_filters_settings.strings.modify_the_filter )
						.set_content( pvam_ajax_filters_settings.strings.please_wait_while_the_dialog_loads )
						.open();

					var type = $button.prop( 'name' );

					// Fetch the filters page.
					$.get( filters_url )
						.done( function( data )
						{
							$page = $( data );

							// Retrieve the form.
							var $form = $( 'form.activity_filters', $page );

							console.log( $form.length );

							// From the form, hide all of the fieldsets.
							$( 'div.fieldset', $form ).hide();

							// Hide the fieldset headers.
							$( 'h3', $form ).hide();

							// Hide the "delete" button.
							$( 'input[name="delete"]', $form ).hide();

							// Unhide the fieldset we're looking for.
							var $fieldset = $( '.fieldset_' + type, $form ).show();

							// The save button we shall override.
							var $save = $( 'input[name="save"]', $form )
								.click( function( save_e )
								{
									save_e.preventDefault();

									$popup.set_content( pvam_ajax_filters_settings.strings.saving );

									var $save = $( this );
									var form_url = $form.prop( 'action' );

									// Assemble the form and send it in.
									$.ajax({
										'data' : $form.serialize() + '&save=save',
										'type' : 'post',
										'url' : form_url,
									} )
									.fail( function( jqXHR )
									{
										$popup.set_title( pvam_ajax_filters_settings.strings.error_saving_your_filter_settings );
										$popup.set_content( jqXHR.responseText );
									} )
									.done( function( data )
									{
										$popup.close();
										pvam_refresh_activities_table();
									});

									return false;
								} );

							// Only insert clear button if there are selects
							var $selects = $( 'select', $fieldset );
							if ( $selects.length > 0 )
							{
								// Insert a clear button next to the save.
								var $clear = $( '<br/>&nbsp;<br/><div class="secondary button">' + pvam_ajax_filters_settings.strings.clear_filters + '</div>' )
									.click( function()
									{
										$selects.val( 'value', [] );
										$save.click();
									} )
									.insertAfter( $save.parents( 'div.input_itself' ) );
							}

							// And put it in the popup.
							$popup.clear_content();
							$form.appendTo( $popup.$content );
						} ); // done
				}

                var filters_url = $( 'a.tab_activity_filters' ).prop( 'href' );

                // Load the CSS file.
                $( 'head' ).append( "<link rel='stylesheet' href='" + pvam_ajax_filters_settings.css + "' type='text/css' media='all' />" );

                var $container = $( '<span class="filters_container">&emsp;Filter: </span>' )
                	.appendTo( $( '.tablenav.top .alignleft', $this ) );

                // Extract the filter settings inputs from the filter settings form.
                $.each( $( 'form.filter_settings input', $this ), function ( index, item )
                {
                	$container.append( ' ' );
                	var $item = $( item );
                	$item
                		// move it to the container
                		.appendTo( $container )
                		.click( function( e )
                		{
                			e.preventDefault();
                			$this.handle_settings( $( this ) );
                		} );
                } );

            }); // return this.each( function()
        } // plugin: function()
    }); // $.fn.extend({
} )( jQuery );
;
/**
	@brief		Convert the form fieldsets in a form2 table to ajaxy tabs.
	@since		2015-07-11 19:47:46
**/
;(function( $ )
{
    $.fn.extend(
    {
        plainview_form_auto_tabs : function()
        {
            return this.each( function()
            {
                var $this = $(this);

                if ( $this.hasClass( 'auto_tabbed' ) )
                	return;

                $this.addClass( 'auto_tabbed' );

				var $fieldsets = $( 'div.fieldset', $this );
				if ( $fieldsets.length < 1 )
					return;

				$this.prepend( '<div style="clear: both"></div>' );
				// Create the "tabs", which are normal Wordpress tabs.
				var $subsubsub = $( '<ul class="subsubsub">' )
					.prependTo( $this );

				$.each( $fieldsets, function( index, item )
				{
					var $item = $(item);
					var $h3 = $( 'h3.title', $item );
					var $a = $( '<a href="#">' ).html( $h3.html() );
					$h3.remove();
					var $li = $( '<li>' );
					$a.appendTo( $li );
					$li.appendTo( $subsubsub );

					// We add a separator if we are not the last li.
					if ( index < $fieldsets.length - 1 )
						$li.append( '<span class="sep">&emsp;|&emsp;</span>' );

					// When clicking on a tab, show it
					$a.click( function()
					{
						$( 'li a', $subsubsub ).removeClass( 'current' );
						$(this).addClass( 'current' );
						$fieldsets.hide();
						$item.show();
					} );

				} );

				$( 'li a', $subsubsub ).first().click();
            } ); // return this.each( function()
        } // plugin: function()
    } ); // $.fn.extend({
} )( jQuery );

;
/**
	@brief		Checkboxize and collapse groups in the table.
	@details

	$( 'table.plugin_pack.with_groups' ).plainview_table_with_groups( {
		'collapsible' : 0,		// How many group items before checkboxing
		'collapse' : 1000,		// How many items before starting collapsed.
	} );

	@since		2015-12-01 17:11:43
**/
;(function( $ )
{
    $.fn.extend(
    {
        plainview_table_with_groups : function( options )
        {
        	options = $.extend( {
        		'collapsible' : 5,		// How many rows to have before the collapse control appears.
        		'collapse' : 10,		// How many rows to have before automatically collapsing on init.
        	}, options );

            return this.each( function()
            {
                var $$ = $(this);

                // Check that we haven't already grouped this table.
                if ( $$.data( 'plainview_table_with_groups' ) == true )
                	return;
                $$.data( 'plainview_table_with_groups', true );

                /**
                	@brief		Add a collapse / uncollapse control for large groups.
                	@since		2015-12-01 16:09:33
                **/
                $$.add_collapsers = function()
                {
					$.each( $( 'tr.group', $$ ), function( index, row )
					{
						var $row = $( row );

						var group = $row.data( 'group' );

						// How many rows have this group?
						var $rows = $( 'tr.plugin[data-group="' + group + '"]' );

						// We need a minimum count.
						if ( $rows.length < options.collapsible )
							return;

						var $th_name = $( 'th.name', $row );

						// Create a collapse / uncollapse
						var $control = $( '<span></span>' )
							.addClass( 'collapser' )
							.data( 'collapsed', false )
							.prependTo( $th_name );

						/**
							@brief		Set the correct arrow when un/collapsing.
							@since		2015-12-04 23:16:16
						**/
						$control.set_html = function()
						{
							var $this = $( this );
							var collapsed = $this.data( 'collapsed' );
							if ( collapsed )
								html = '<span class="dashicons dashicons-arrow-down-alt2"></span>';
							else
								html = '<span class="dashicons dashicons-arrow-up-alt2"></span>';

							$this.html( html + '&emsp;' );
						}

						/**
							@brief		Set the correct un/collapsed class for the row.
							@since		2015-12-04 23:16:34
						**/
						$row.set_collapsed_class = function( collapsed )
						{
							if ( collapsed )
								$row.addClass( 'collapsed' ).removeClass( 'uncollapsed' );
							else
								$row.removeClass( 'collapsed' ).addClass( 'uncollapsed' );
						}

						/**
							@brief		Un/collapse when clicking.
							@since		2015-12-04 23:16:48
						**/
						$th_name.click( function()
							{
								var collapsed = $control.data( 'collapsed' );
								collapsed = ! collapsed;

								$control.data( 'collapsed', collapsed );
								$row.set_collapsed_class( collapsed );

								var $check_column = $( 'th.check-column input', $row );
								if ( ! collapsed )
									$check_column.fadeTo( 'fast', 1.0 );
								else
									$check_column.fadeTo( 'fast', 0 );

								$rows.toggle( 'fast', function()
								{
									$control.set_html();
								});
							} )
							.css( 'cursor', 'pointer' );

						if ( $rows.length >= options.collapse )
							$th_name.click();
						else
						{
							$control.set_html();
							$row.set_collapsed_class( false );
						}

						// It's a good idea to have an active / inactive counter.
						var $active = $( 'tr.plugin.active[data-group="' + group + '"]' );
						$( 'th.name', $row ).append( ' <small>' + $active.length + ' / ' + $rows.length + '</small>');
					} );
				}

                /**
                	@brief		Add a group checkbox.
                	@since		2015-12-01 16:08:35
                **/
                $$.add_group_cb = function()
                {
					// Find all group rows.
					$.each( $( 'tr.group', $$ ), function( index, row )
					{
						var $row = $( row );

						// Make the colspan one less, due to the upcoming cb.
						var $th = $( 'th', $row );
						var colspan = $th.prop( 'colspan' );
						colspan = colspan - 1;
						$th.prop( 'colspan', colspan );
						$th.addClass( 'name' );

						// Insert a checkbox.
						var $th_cb = $( '<th class="check-column"></th>' );
						var $cb = $( '<input type="checkbox" scope="row">' ).click( function()
						{
							var $this = $( this );

							// What is our state?
							var checked = $this.prop( 'checked' );

							// Find our group slug.
							var group_slug = $this.parent().parent().data( 'group' );

							// Check / uncheck "our" checkboxes
							var selector = 'tr.plugin[data-group="' + group_slug + '"] th.check-column input';
							$( selector, $$ ).prop( 'checked', checked );
						} );
						$cb.appendTo( $th_cb );
						$th_cb.prependTo( $row );
					} );
                }

				/**
					@brief		Add un/collapsers for the entire table.
					@since		2015-12-04 23:18:30
				**/
				$$.add_table_collapsers = function()
				{
					var $table_collapsers = $( '<p></p>' )
						.css( 'cursor', 'pointer' )
						.appendTo( $$.parent() );

					var $table_uncollapser = $( '<span><small><span class="dashicons dashicons-arrow-down-alt2"></span> Expand all</small></span>' )
						.click( function()
						{
							$( '.collapser', $$ ).map( function()
							{
								var $this = $( this );
								if ( $this.data( 'collapsed' ) == true )
									$this.click();
							} );
						} )
						.appendTo( $table_collapsers );

					$table_collapsers.append( '&emsp;&emsp;' );

					var $table_collapser = $( '<span><small><span class="dashicons dashicons-arrow-up-alt2"></span> Collapse all</small></span>' )
						.click( function()
						{
							$( '.collapser', $$ ).map( function()
							{
								var $this = $( this );
								if ( $this.data( 'collapsed' ) == false )
									$this.click();
							} );
						} )
						.appendTo( $table_collapsers );
					// end: Add expanders and collapsers for the entire table.
                }

                /**
                	@brief		Steal the color from the adminmenu and use it as a group background.
                	@since		2015-12-04 22:56:32
                **/
                $$.steal_colors = function()
                {
                	// Extract the colors of the active menu item.
                	var color = $( '#adminmenu a.wp-menu-open' ).css( 'color' );
                	var background_color = $( '#adminmenu a.wp-menu-open' ).css( 'background-color' );

                	// Assemble the style text.
                	var text = 'table.with_groups tr.group.uncollapsed { background-color : ' + background_color + '; }';
                	text += 'table.with_groups tr.group.uncollapsed th { color: ' + color + '; }';

                	// Create the style element and put it before the table.
                	var $style = $( '<style>' )
                		.text( text )
                		.prependTo( $$.parent() );
                }

                // The group cb adds the name class, which is required for the collapsers.
                $$.add_group_cb();
                $$.add_collapsers();
                $$.add_table_collapsers();
                $$.steal_colors();

            } ); // return this.each( function()
        } // plugin: function()
    } ); // $.fn.extend({
} )( jQuery );
;
jQuery(document).ready( function( $ )
{
	$( '.pvam_overview_table' ).pvam_ajax_filters();
	$( '.pvam_overview_table' ).pvam_autorefresh_activities_table();
	$( 'form.plainview_form_auto_tabs' ).plainview_form_auto_tabs();
	$( 'table.pvam.with_groups' ).plainview_table_with_groups( {
		'collapsible' : 0,
		'collapse' : 15,
	} );	// Only the activities table.
	$( 'table.plugin_pack.with_groups' ).plainview_table_with_groups( {
		'collapsible' : 0,
		'collapse' : 1000,
	} );	// Only the plugin pack table. Do not collapse automatically.
} );
